/*
网络变化监控 For Quantumult X
[rewrite_local]
event-network script-path=network-monitor.js, tag=网络监测, enabled=true
*/

const $ = new Env('网络监测')

let networkState = {
    lastType: '', // 上一次的网络类型
    lastStatus: '', // 上一次的连接状态
    failCount: 0 // 连续失败次数
}

// 主函数
async function networkCheck() {
    try {
        // 使用 ip-api.com 检查网络状态和类型
        const res = await $.http.get({
            url: 'http://ip-api.com/json?lang=zh-CN',
            timeout: 3000
        })
        
        const data = JSON.parse(res.body)
        const currentNetworkType = data.mobile ? 'cellular' : 'wifi'
        
        // 检查网络类型变化
        if (networkState.lastType !== currentNetworkType) {
            if (currentNetworkType === 'wifi') {
                $.msg('网络变化提醒 🔄', '', '当前网络已切换至 WiFi')
            } else {
                $.msg('网络变化提醒 🔄', '', '当前网络已切换至蜂窝数据')
            }
            networkState.lastType = currentNetworkType
        }
        
        // 网络连接状态检测
        if (res.statusCode === 200) {
            if (networkState.failCount > 0) {
                $.msg('网络状态提醒 ✅', '', '网络已恢复正常') 
                networkState.failCount = 0
            }
            networkState.lastStatus = 'connected'
        }
        
    } catch (error) {
        networkState.failCount++
        if (networkState.failCount >= 3 && networkState.lastStatus === 'connected') {
            $.msg('网络状态提醒 ⚠️', '', '当前网络不稳定')
            networkState.lastStatus = 'unstable'
        }
    }
    
    // 进行网络速度测试
    try {
        const startTime = new Date().getTime()
        await $.http.get({
            url: 'http://www.gstatic.com/generate_204',
            timeout: 3000
        })
        const endTime = new Date().getTime()
        const speed = endTime - startTime
        
        if (speed > 1000) { // 响应时间超过1秒
            $.msg('网络状态提醒 🐌', '', `网络延迟较高: ${speed}ms`)
        }
    } catch (error) {
        // 速度测试失败，已在上面的错误处理中处理
    }
}

// 执行检测
!(async () => {
    await networkCheck()
})().catch((e) => {
    $.logErr(e)
}).finally(() => {
    $.done()
})

